const path = require('path')
const webpackMerge = require('webpack-merge')
const MiniCssExtractPlugin = require('mini-css-extract-plugin')
const commonConfig = require('./webpack.common')

module.exports = webpackMerge(commonConfig, {
  // The mode property includes some environment-specific Webpack config, based on its value.
  // Docs: https://webpack.js.org/concepts/mode/
  mode: 'production',

  output: {
    // output.path specifies where to store the artifacts generated by Webpack.
    // While using Webpack Dev Server, this folder is generated in memory only.
    path: path.resolve(__dirname, './../dist')
  },

  module: {
    rules: [
      // Load vendor and shared (App.css) styles globally.
      {
        test: /\.css$/,
        use: [MiniCssExtractPlugin.loader, 'css-loader'],
        include: [/node_modules/],
        exclude: [/src\/styles.css/]
      },
      {
        test: /\.css$/,
        use: [
          MiniCssExtractPlugin.loader,
          'css-loader',
          {
            loader: 'postcss-loader',
            options: { config: { path: './config/' } }
          }
        ],
        include: [/src\/styles.css/],
        exclude: [/node_modules/]
      },
      // Load all other styles as CSS Modules, which allows for styled React components.
      {
        test: /\.css$/,
        use: [
          'style-loader',
          {
            loader: 'css-loader',
            query: {
              modules: true,
              localIdentName: '[name]__[local]___[hash:base64:5]'
            }
          },
          {
            loader: 'postcss-loader',
            options: { config: { path: './config/' } }
          }
        ],
        exclude: [/node_modules/, /src\/styles.css/]
      }
    ]
  },

  plugins: [
    // MiniCssExtractPlugin allows us to extract styles into isolated css files.
    new MiniCssExtractPlugin({
      filename: 'styles/[name].[hash].css'
    })
  ]
})
