const path = require('path')
const webpackMerge = require('webpack-merge')
const commonConfig = require('./webpack.common')

module.exports = webpackMerge(commonConfig, {
  // The mode property includes some environment-specific Webpack config, based on its value.
  // Docs: https://webpack.js.org/concepts/mode/
  mode: 'production',

  output: {
    // output.path specifies where to store the artifacts generated by Webpack.
    // While using Webpack Dev Server, this folder is generated in memory only.
    path: path.resolve(__dirname, './../dist')
  },

  module: {
    rules: [
      // CSS imports from files within the app code are saved in the React app itself.
      {
        test: /\.css$/,
        use: [
          'style-loader',
          { loader: 'css-loader', options: { minimize: true } },
          {
            loader: 'postcss-loader',
            options: { config: { path: './config/postcss.config.js' } }
          }
        ],
        exclude: [/node_modules/]
      }
    ]
  }
})
